import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor() {
    super();
    this.state = {
      varValue: 'Hello Mr. World',

      monster: [
        {
          name: 'Botch',
          Id: 1
        },
        {
          name: 'Flatter',
          Id: 2
        },
        {
          name: 'Read',
          Id: 2
        }
      ]

    };
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h1 >{this.state.varValue}</h1>

          {
            this.state.monster.map(monster => {
              <h1 >{monster.name}</h1>
            })
          }

          <button onClick={() => this.setState({ varValue: 'Hello Ashwini' })}>
            Click Me
        </button>
        </header>
      </div>
    );
  }
}
export default App;
